import React, { useState } from 'react';
import {Skeleton} from './Skeleton'
import { User } from './User';

export const Users = ({ items, isLoading }) => {
  const [findPost, setFindPost] = useState('');
  const [filterPost, setFilterPost] = useState('')



  const findPosts=(post)=>{
    return post.filter(post=>post.first_name.toLowerCase().includes(findPost));
  }

  const inputInfo = (findPost)=>{
    setFindPost(findPost);


    setFilterPost(findPosts(items))
}
  return (
    <>
      <div className="search">
        <svg viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
          <path d="M12.9 14.32a8 8 0 1 1 1.41-1.41l5.35 5.33-1.42 1.42-5.33-5.34zM8 14A6 6 0 1 0 8 2a6 6 0 0 0 0 12z" />
        </svg>
        <input onChange={(e)=>inputInfo(e.target.value)} type="text" placeholder="Найти пользователя..." />
      </div>
      {isLoading ? (
        <div className="skeleton-list">
          <Skeleton />
          <Skeleton />
          <Skeleton />
        </div>
      ) : (
        <ul className="users-list">

          {items.map(e=>

            <User key={e.id} name={e.first_name} email={e.email} avatar={e.avatar}/>
          )}
        </ul>
      )}
      <button className="send-invite-btn">Отправить приглашение</button>
    </>
  );
};
